mapper.select:

	select count(*) cnt from LODGE WHERE LODCATE='호텔' AND LODNAME like '%'||#{searchtext}||'%';
select * from LODGE where LODID in
(select LODID from (select rownum as rnum, LODID 
from (select LODID from LODGE WHERE LODCATE='호텔'  AND LODNAME like '%'||#{searchtext}||'%' order by LODID desc)) where rnum>=1 and rnum<=8)
order by LODID desc
